mapper.select:
select a.app_id as app_id, a.draft_dt as draft_dt, at.app_type_nm as app_type_nm, d.dept_nm as dept_nm, ed.emp_nm as draft_emp_nm , ea.emp_nm as app_emp_nm, ap.app_pro_nm as app_pro_nm from approval a inner join app_type at on a.app_type_cd = at.app_type_cd and a.draft_emp_id = #{emp_id} inner join app_pro_type ap on a.app_pro_cd = ap.app_pro_cd inner join dept d on a.dept_id = d.dept_id inner join emp ed on a.draft_emp_id = ed.emp_id inner join emp ea on a.app_emp_id = ea.emp_id
and
a.${item} LIKE #{app_id_search}
a.${item} = #{app_type}
a.${item}= #{dept}
( a.${item} LIKE #{draft_emp_id_search} or ed.emp_nm LIKE #{draft_emp_id_search})
( a.${item} LIKE #{app_emp_id_search} or ea.emp_nm LIKE #{app_emp_id_search})
a.${item} between #{draft_s_dt} and #{draft_e_dt}
order by a.app_id desc

select a.app_id as app_id, a.draft_dt as draft_dt, at.app_type_nm as app_type_nm, d.dept_nm as dept_nm, ed.emp_nm as draft_emp_nm , ea.emp_nm as app_emp_nm, ap.app_pro_nm as app_pro_nm from approval a inner join app_type at on a.app_type_cd = at.app_type_cd and a.draft_emp_id = #{emp_id} inner join app_pro_type ap on a.app_pro_cd = ap.app_pro_cd inner join dept d on a.dept_id = d.dept_id inner join emp ed on a.draft_emp_id = ed.emp_id inner join emp ea on a.app_emp_id = ea.emp_id and a.app_pro_cd = '1'
and
a.${item} LIKE #{app_id_search}
a.${item} = #{app_type}
a.${item}= #{dept}
( a.${item} LIKE #{draft_emp_id_search} or ed.emp_nm LIKE #{draft_emp_id_search})
( a.${item} LIKE #{app_emp_id_search} or ea.emp_nm LIKE #{app_emp_id_search})
a.${item} between #{draft_s_dt} and #{draft_e_dt}
order by a.app_id desc

