select count(*) from approval

	
		where draft_emp_id = #{emp_id}
	
	
		where draft_emp_id = #{emp_id}
		  and app_pro_cd in ('1')
	
	
		where draft_emp_id = #{emp_id}
		  and app_pro_cd in ('2')
	
	
		where draft_emp_id = #{emp_id}
		  and app_pro_cd in ('3','4')
	
	
		where draft_emp_id = #{emp_id}
	
	
		where app_emp_id = #{emp_id}
	
	
		where app_emp_id = #{emp_id}
		  and app_pro_cd in ('0')
	
	
		where app_emp_id = #{emp_id}
		  and app_pro_cd in ('1')
	
	
		where app_emp_id = #{emp_id}
		  and app_pro_cd in ('2')
	
	
		where app_emp_id = #{emp_id}
		  and app_pro_cd in ('3','4')
	


	
		and ${item} LIKE #{searchKey}
