mapper.insert:
		insert into noticeboard(
         n_id, n_emp_id, n_title, n_content, n_date, n_hit
      )
      values(
         nboard.nextval, #{n_emp_id}, #{n_title}, #{n_content}, SYSDATE, 0
      )

mapper.select:
select * from noticeboard


	
		            		
			${item} LIKE #{searchKey}		
		
	
	


order by n_id desc

mapper.update:
update noticeboard set 
	n_title = #{n_title}, 
	n_content = #{n_content}
where n_id = #{n_id}

update noticeboard set
	n_hit = n_hit + 1
where n_id = #{n_id}
